Method.Implementation:
Set tSC = $System.Status.OK()
Try
{
	If $System.Mirror.IsMember() && '$System.Mirror.IsPrimary()
	{
		//If we are part of a mirror and we are not the primary, we should not run right now.
		Quit
	}

	If '##class(%SYS.Namespace).Exists(..Namespace) 
	{
		Set tSC = $$$ERROR($$$NamespaceUnavailable,..Namespace)
		Quit
	}

	Set tOwner = $ZConvert($p(..GitHubURL,"/",4),"L")
	Set tRepository = $ZConvert($p(..GitHubURL,"/",5),"L")
	Set tNamespace = $ZConvert(..Namespace,"L")

	If ('##class(CacheGitHubCI.Hook).%ExistsId(tOwner_"||"_tRepository_"||"_tNamespace)) 
	{
		Set hook = ##class(CacheGitHubCI.Hook).%New(tOwner,tRepository,..Branch,..Username,..Password,tNamespace)
		Set tSC = hook.%Save()
		Quit:$System.Status.IsError(tSC)
	} 

	Set tSC = ##class(CacheGitHubCI.Hook).Update(tOwner,tRepository,tNamespace)
}
Catch (oException)
{
	Set tSC = oException.AsStatus()
}

Quit tSC
